mapper:







//根据学号删除

    delete from canteen_admin
    where ad_id = #{adID,jdbcType=INTEGER}


//插入食堂管理员信息

    insert into canteem_admin (ad_id,ad_username, ad_password, ad_canteen_id)
    values (#{adID,jdbcType=INTEGER},#{adUsername,jdbcType=VARCHAR}, #{adPassword,jdbcType=VARCHAR}, #{adCanteenID,jdbcType=INTEGER})


//根据工号更新信息

    update canteen_admin
    set ad_username = #{adUsername,jdbcType=VARCHAR},
        ad_password = #{adPassword,jdbcType=VARCHAR},
        ad_canteen_id = #{adCanteenID,jdbcType=INTEGER}
    where ad_id = #{adID,jdbcType=INTEGER}


//查询所有人信息

    select ad_id, ad_username, ad_password, ad_canteen_id
    from canteen_admin
    where 1=1

        and ad_username like concat('%',#{adUsername},'%')



//登陆时查询

    select ad_id, ad_username, ad_password, ad_canteen_id
    from canteen_admin
    where 1=1

        and ad_id =#{adID}


        and ad_password =#{adPassword}
